import { useReducer } from "react";
import CartContext from "./cart-context";

const defaultCartState = {
  items: [],
  totalAmount: 0,
};

function cartReducer(state, action) {
  switch (action.type) {
    case "ADD":
      const updatedTotalAmount =
        state.totalAmount + action.item.price * action.item.amount;

      const existingCartItemIndex = state.items.findIndex((item) => item.id === action.item.id);
      const existingCartItem = state.items[existingCartItemIndex];

      let updatedItems;
      if (existingCartItem) {
        // if the new item is already in the list
        let newCartItem = {
          ...existingCartItem,
          amount: existingCartItem.amount + action.item.amount,
        };
        newCartItem = [...state.items];
        updatedItems[existingCartItemIndex] = state.items.concat(action.item);
      } else {
        updatedItems = state.items.concat(action.item);
      }
      return {
        items: updatedItems,
        totalAmount: updatedTotalAmount,
      };
    case "REMOVE":
      break;
    default:
      return defaultCartState;
  }
}

export default function CartProvider(props) {
  const [cartState, dispatchCartAction] = useReducer(
    cartReducer,
    defaultCartState
  );

  function addItemToCartHandler(_item) {
    dispatchCartAction({ type: "ADD", item: _item });
  }

  function removeItemFromCartHandler(_id) {
    dispatchCartAction({ type: "REMOVE", id: _id });
  }

  const cartContext = {
    items: cartState.items,
    totalAmount: cartState.totalAmount,
    addItem: addItemToCartHandler,
    removeItem: removeItemFromCartHandler,
  };

  return (
    <CartContext.Provider value={cartContext}>
      {props.children}
    </CartContext.Provider>
  );
}
